import { Component, OnInit } from '@angular/core';
import { TodoService } from '../service/todo.service';
import { Users } from 'src/interfaces/users';
import { Todo } from 'src/interfaces/todo';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.scss'],
})
export class UsersComponent implements OnInit {
  todos: Todo[] = [];
  users: Users[] = [];
  filteredTodos: Todo[] = [];
  searchUserName: string = '';

  constructor(private todosService: TodoService) {}

  ngOnInit(): void {
    this.todosService.getTodos().subscribe(
      (todos: Todo[]) => {
        this.todos = todos;
        this.filteredTodos = todos;
      },
      error => {
        console.error('Errore durante il recupero dei todo:', error);
      }
    );

    this.todosService.getUsers().subscribe(
      (users: Users[]) => {
        this.users = users;
      },
      error => {
        console.error('Errore durante il recupero degli utenti:', error);
      }
    );
  }

  getUserById(userId: number): Users | undefined {
    return this.users.find(user => user.id === userId);
  }

  updateTodoStatus(todo: Todo): void {
    todo.completed = !todo.completed;
    this.todosService.updateTodoLocal(todo).subscribe(
      updatedTodo => {
        console.log('Todo aggiornato:', updatedTodo);
        const index = this.todos.findIndex(t => t.id === updatedTodo.id);
        if (index !== -1) {
          this.todos[index] = updatedTodo;
          const filteredIndex = this.filteredTodos.findIndex(td => td.id === updatedTodo.id);
          if (filteredIndex !== -1) {
            this.filteredTodos[filteredIndex] = updatedTodo;
          }
        }
      },
      error => {
        console.error('Errore durante l\'aggiornamento del todo:', error);
        todo.completed = !todo.completed;
      }
    );
  }
}
<div class="container-fluid pb-4 mt-4 sticky-top bg-white">
  <h1>Hey Boss, what's my task for the day?</h1>
  <div>
    <div class="d-inline-block mx-2">
      <a [routerLink]="['/completed']" class="btn btn-primary">
        See how much we've accomplished!
      </a>
    </div>
    <div class="d-inline-block mx-2">
      <a [routerLink]="['/notCompleted']" class="btn btn-primary">
        What's left?
      </a>
    </div>
    <div class="d-inline-block mx-2">
      <a [routerLink]="['/users']" class="btn btn-primary"> By User </a>
    </div>
    <div class="d-inline-block mx-2">
      <a [routerLink]="['/']" class="btn btn-primary"> See all </a>
    </div>
  </div>
</div>
<div class="container-fluid">
  <div class="row bg-white rounded-3 p-5 mb-0" *ngFor="let user of users">
    <div class="col-12 border-container">
      <div class="row">
        <div class="col-4">
          <div>
            <img
              class="img-fluid"
              src="{{ user.image }}"
              alt="foto profilo {{ user.firstName }} {{ user.lastName }}"
            />
          </div>
        </div>
        <div class="col-8 px-4 pb-0 mt-0">
          <h3>{{ user.firstName }} {{ user.lastName }}</h3>
          <p>{{ user.email }}</p>
          <div class="row">
            <div class="col-12" *ngFor="let todo of filteredTodos">
              <div
                class="row my-1 rounded-4"
                *ngIf="todo.userId === user.id"
                [ngStyle]="{
                  'background-color': todo.completed ? '#98f49e' : '#f47474'
                }"
              >
                <div class="col-2 p-3">
                  <div
                    class="h-100 d-flex justify-content-center align-items-center"
                  >
                    <input
                      type="checkbox"
                      [checked]="todo.completed"
                      (change)="updateTodoStatus(todo)"
                    />
                  </div>
                </div>
                <div class="col-10 p-2">
                  <div class="row h-100 d-flex align-items-center">
                    <h4 class="col-12 m-0">{{ todo.todo }}</h4>
                    <p class="col-12 m-0">
                      Asigned to: {{ getUserById(todo.userId)?.firstName }}
                      {{ getUserById(todo.userId)?.lastName }}
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
